import React, { useState } from 'react';
import CryptoJS from 'crypto-js';

// Mock user database with hashed passwords
const STAFF_USERS = [
  {
    id: 1,
    username: 'מנהלת',
    passwordHash: CryptoJS.SHA256('123456' + 'salt123').toString(),
    role: 'manager'
  },
  {
    id: 2,
    username: 'גננת1',
    passwordHash: CryptoJS.SHA256('password' + 'salt123').toString(),
    role: 'caregiver'
  }
];

const PARENT_USERS = [
  {
    id: 1,
    parentCode: 'PARENT001',
    passwordHash: CryptoJS.SHA256('noa123' + 'salt123').toString(),
    childName: 'נועה'
  },
  {
    id: 2,
    parentCode: 'PARENT002',
    passwordHash: CryptoJS.SHA256('ariel123' + 'salt123').toString(),
    childName: 'אריאל'
  },
  {
    id: 3,
    parentCode: 'PARENT003',
    passwordHash: CryptoJS.SHA256('tamar123' + 'salt123').toString(),
    childName: 'תמר'
  },
  {
    id: 4,
    parentCode: 'PARENT004',
    passwordHash: CryptoJS.SHA256('yonatan123' + 'salt123').toString(),
    childName: 'יונתן'
  }
];

const Authentication = ({ userType, onAuthentication, onBack }) => {
  const [formData, setFormData] = useState({
    username: '',
    password: '',
    parentCode: ''
  });
  const [error, setError] = useState('');
  const [isLoading, setIsLoading] = useState(false);

  const handleInputChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value
    });
    setError('');
  };

  const hashPassword = (password) => {
    return CryptoJS.SHA256(password + 'salt123').toString();
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setIsLoading(true);
    setError('');

    // Debug logging
    console.log('Attempting login with:', {
      userType,
      formData,
      parentCode: formData.parentCode,
      password: formData.password
    });

    // Simulate loading time for better UX
    await new Promise(resolve => setTimeout(resolve, 1000));

    try {
      if (userType === 'staff') {
        const hashedPassword = hashPassword(formData.password);
        const user = STAFF_USERS.find(
          u => u.username === formData.username && u.passwordHash === hashedPassword
        );

        if (user) {
          onAuthentication({
            id: user.id,
            username: user.username,
            role: user.role,
            type: 'staff'
          });
        } else {
          setError('שם משתמש או סיסמה שגויים');
        }
      } else if (userType === 'parent') {
        const hashedPassword = hashPassword(formData.password);
        console.log('Hashed password:', hashedPassword);

        const user = PARENT_USERS.find(
          u => u.parentCode === formData.parentCode && u.passwordHash === hashedPassword
        );

        console.log('Found user:', user);
        console.log('Available users:', PARENT_USERS);

        if (user) {
          onAuthentication({
            id: user.id,
            parentCode: user.parentCode,
            childName: user.childName,
            type: 'parent'
          });
        } else {
          setError('קוד הורה או סיסמה שגויים');
        }
      }
    } catch (err) {
      console.error('Login error:', err);
      setError('שגיאה במערכת, נסה שוב');
    } finally {
      setIsLoading(false);
    }
  };

  return (
    <div className="auth-screen">
      <button className="btn btn-back" onClick={onBack}>
        ← חזרה
      </button>

      <h2 className="auth-title">
        {userType === 'staff' ? 'כניסת צוות המעון' : 'כניסת הורים'}
      </h2>

      <form className="auth-form" onSubmit={handleSubmit}>
        {userType === 'staff' ? (
          <>
            <div className="form-group">
              <label className="form-label">שם משתמש</label>
              <input
                type="text"
                name="username"
                className="form-input"
                value={formData.username}
                onChange={handleInputChange}
                placeholder="הכנס שם משתמש"
                required
              />
            </div>
            <div className="form-group">
              <label className="form-label">סיסמה</label>
              <input
                type="password"
                name="password"
                className="form-input"
                value={formData.password}
                onChange={handleInputChange}
                placeholder="הכנס סיסמה"
                required
              />
            </div>
          </>
        ) : (
          <>
            <div className="form-group">
              <label className="form-label">קוד הורה</label>
              <input
                type="text"
                name="parentCode"
                className="form-input"
                value={formData.parentCode}
                onChange={handleInputChange}
                placeholder="הכנס קוד הורה (לדוגמה: PARENT001)"
                required
              />
            </div>
            <div className="form-group">
              <label className="form-label">סיסמה</label>
              <input
                type="password"
                name="password"
                className="form-input"
                value={formData.password}
                onChange={handleInputChange}
                placeholder="הכנס סיסמה"
                required
              />
            </div>
          </>
        )}

        {error && <div className="error-message">{error}</div>}

        <button 
          type="submit" 
          className="btn btn-primary"
          disabled={isLoading}
        >
          {isLoading ? 'מתחבר...' : 'התחבר'}
        </button>
      </form>

      {/* Demo credentials info */}
      <div style={{ 
        marginTop: '30px', 
        padding: '15px', 
        background: '#F7FAFC', 
        borderRadius: '10px',
        fontSize: '0.9rem',
        color: '#6B7280'
      }}>
        <strong>פרטי התחברות לדמו:</strong><br/>
        {userType === 'staff' ? (
          <>
            צוות: מנהלת / 123456<br/>
            צוות: גננת1 / password
          </>
        ) : (
          <>
            הורה: PARENT001 / noa123<br/>
            הורה: PARENT002 / ariel123<br/>
            הורה: PARENT003 / tamar123<br/>
            הורה: PARENT004 / yonatan123
          </>
        )}
      </div>
    </div>
  );
};

export default Authentication;
